import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private router: Router) {}

  showNavbar(): boolean {
    return !(
      this.router.url.includes('login') ||
      this.router.url.includes('select-role')
    );
  }

  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  getUserName(): string {
    return localStorage.getItem('fullName') || "Profile";
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }

  isLoggedIn(): boolean {
  return !!localStorage.getItem("token"); // or use your auth service
}


}

<section class="hero">
  <div class="hero-left">
    <h1>10-Day Masterclass for Hair Stylists</h1>
    <p>Learn advanced techniques from industry experts.</p>

    <button routerLink="/user/book" class="book-btn">Book Now</button>
  </div>

  <div class="hero-img-box">
    <img src="assets/Images/Hero-model.jpg" alt="Hair Stylist" />
  </div>
</section>

<section class="batches">
  <h2>Upcoming Masterclass Batches</h2>

  <div class="batch-card">
    <h3>May 15 – May 24</h3>
    <p>10-Day Course</p>

    <div class="batch-info">
      <span>5 seats remaining</span>
      <button routerLink="/user/book" class="view-btn">View Batches</button>
    </div>
  </div>
</section>
