import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private router: Router) {}

  showNavbar(): boolean {
    return !(
      this.router.url.includes('login') ||
      this.router.url.includes('select-role')
    );
  }

  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  getUserName(): string {
    return localStorage.getItem('fullName') || "Profile";
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }

  isLoggedIn(): boolean {
  return !!localStorage.getItem("token"); // or use your auth service
}


}
<!-- NAVBAR -->
<nav class="navbar navbar-expand-lg main-navbar" *ngIf="showNavbar()">
  <div class="container-fluid">

    <!-- Logo -->
    <a class="navbar-brand fw-bold text-white" routerLink="/home">
      <i class="bi bi-scissors me-1"></i> Hairstylist App
    </a>

    <div class="collapse navbar-collapse">

      <ul class="navbar-nav me-auto">

        <!-- HOME always visible after login -->
        <li class="nav-item">
          <a class="nav-link" routerLink="/home">Home</a>
        </li>

        <!-- USER LINKS -->
        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/book">Book Batch</a>
        </li>

        <li class="nav-item" *ngIf="isUser()">
          <a class="nav-link" routerLink="/user/bookings">My Bookings</a>
        </li>

        <!-- INSTRUCTOR LINKS -->
        <li class="nav-item" *ngIf="isInstructor()">
          <a class="nav-link" routerLink="/instructor/manage-batches">Manage Batches</a>
        </li>

      </ul>

      <!-- RIGHT SIDE PROFILE OR LOGIN -->
      <div *ngIf="!isLoggedIn()">
        <button class="btn btn-primary-custom" routerLink="/login">Login</button>
      </div>

      <div class="dropdown" *ngIf="isLoggedIn()">
        <button class="btn btn-light dropdown-toggle" data-bs-toggle="dropdown">
          <i class="bi bi-person-circle me-1"></i> {{ getUserName() }}
        </button>
        <ul class="dropdown-menu dropdown-menu-end">
          <li><a class="dropdown-item" routerLink="/profile">My Profile</a></li>
          <li><button class="dropdown-item text-danger" (click)="logout()">Logout</button></li>
        </ul>
      </div>

    </div>

  </div>
</nav>

<!-- LOAD OTHER PAGES -->
<router-outlet></router-outlet>
