import { Component, OnInit } from '@angular/core';
import { BookingService } from '../../services/booking.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-my-bookings',
  templateUrl: './my-bookings.component.html',
  styleUrls: ['./my-bookings.component.css']
})
export class MyBookingsComponent implements OnInit {

  bookings: any[] = [];

  constructor(
    private bookingService: BookingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBookings();
  }

  loadBookings() {
    const userId = localStorage.getItem('userId');

    if (!userId) {
      alert('Please login again.');
      this.router.navigate(['/login']);
      return;
    }

    this.bookingService.getBookingsByUser(userId).subscribe({
      next: (res: any[]) => {
        // ✅ Use backend status as-is
        this.bookings = res;
      },
      error: (err) => console.error(err)
    });
  }

  makePayment(booking: any) {
    this.router.navigate(['/user/payment'], {
      state: {
        bookingId: booking.bookingId,
        amount: booking.batch.price
      }
    });
  }

  cancelBooking(booking: any) {
    if (!confirm('Are you sure you want to cancel this booking?')) return;

    this.bookingService.cancelBooking(booking.bookingId).subscribe({
      next: () => {
        booking.status = 'Cancelled';
      },
      error: (err) => {
        console.error(err);
        alert(err.error?.message || 'Failed to cancel booking');
      }
    });
  }
}
<div class="container mt-4">
  <!-- Page Heading -->
  <h2 class="page-heading mb-4">My Bookings</h2>

  <!-- No bookings -->
  <div *ngIf="bookings.length === 0" class="text-center text-muted mt-5">
    <i class="bi bi-info-circle fs-2"></i>
    <p class="mt-2">You have not booked any batches yet.</p>
  </div>

  <!-- Bookings Grid -->
  <div class="row g-4" *ngIf="bookings.length > 0">
    <ng-container *ngFor="let booking of bookings">
      <div class="col-md-4" *ngIf="booking.status !== 'Cancelled'">
        <div class="card card-custom shadow-sm">

          <h5 class="fw-bold">{{ booking.batch?.batchName }}</h5>

          <p class="text-muted mb-1">
            <i class="bi bi-person-badge"></i>
            Instructor: {{ booking.batch?.instructor?.name }}
          </p>

          <p class="text-muted mb-1">
            <i class="bi bi-calendar-event"></i>
            Start Date: {{ booking.batch?.startDate | date }}
          </p>

          <p class="mb-2">
            <i class="bi bi-clock-history"></i>
            Booked on: {{ booking.bookingDate | date }}
          </p>

          <!-- ✅ STATUS BADGE -->
          <span
            class="badge px-3 py-2"
            [ngClass]="{
              'bg-warning text-dark': booking.status === 'Pending',
              'bg-success': booking.status === 'Confirmed',
              'bg-secondary': booking.status === 'Cancelled'
            }">
            {{
              booking.status === 'Confirmed'
                ? 'Booked'
                : booking.status
            }}
          </span>

          <!-- ✅ ACTIONS -->
          <div class="d-flex gap-2 mt-3">

            <!-- Pay Now (ONLY Pending) -->
            <button
              *ngIf="booking.status === 'Pending'"
              class="btn btn-primary-custom flex-fill"
              (click)="makePayment(booking)">
              Pay Now
            </button>

            <!-- Cancel (ONLY Pending) -->
            <button
              *ngIf="booking.status === 'Pending'"
              class="btn btn-cancel-custom flex-fill"
              (click)="cancelBooking(booking)">
              Cancel
            </button>

            <!-- Booked (ONLY Confirmed) -->
            <button
              *ngIf="booking.status === 'Confirmed'"
              class="btn btn-success flex-fill"
              disabled>
              Booked
            </button>

          </div>

        </div>
      </div>
    </ng-container>
  </div>
</div>
