import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {

  constructor(private router: Router) {}

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }
}
<header class="app-header d-flex justify-content-between align-items-center px-4 py-3 shadow-sm">

  <!-- Logo or App Name -->
  <div class="logo">
    <h3 class="m-0 text-primary">HairStylist Amar</h3>
  </div>

  <!-- Profile Menu -->
  <div class="profile-wrapper position-relative">

    <div class="profile-btn d-flex align-items-center"
         (click)="toggleMenu()">

      <i class="bi bi-person-circle fs-4 me-2"></i>
      <span class="fw-semibold">Profile</span>
      <i class="bi bi-caret-down-fill ms-1"></i>
    </div>

    <!-- Dropdown -->
    <div class="dropdown-menu-custom"
         *ngIf="menuOpen">

      <a (click)="goToProfile()">View Profile</a>
      <a (click)="logout()">Logout</a>

    </div>

  </div>

</header>
