import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { API } from 'src/app/core/api/api-constants.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css']
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  selectedService: any = null;
  batches: any[] = [];

  loading = true;

  constructor(
    private http: HttpClient,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  loadServices(): void {
    this.http.get<any[]>(`${API.services}`).subscribe({
      next: res => {
        this.services = res;
        this.loading = false;
      },
      error: err => {
        console.error('Failed to load services', err);
        this.loading = false;
      }
    });
  }

  selectService(service: any): void {
    this.selectedService = service;
    this.loadBatches(service.serviceId);
  }

  loadBatches(serviceId: string): void {
    this.http.get<any[]>(`${API.batches}/service/${serviceId}`).subscribe({
      next: res => this.batches = res,
      error: err => console.error('Failed to load batches', err)
    });
  }

  bookBatch(batchId: string): void {
    this.router.navigate(['/user/book', batchId]);
  }
}
<div class="book-batch-page">

  <h2 class="page-title">Book a Batch</h2>

  <!-- SERVICES -->
  <div class="services-grid" *ngIf="!loading">
    <div
      class="service-card"
      *ngFor="let service of services"
      (click)="selectService(service)"
      [class.active]="service === selectedService"
    >
      <h4>{{ service.name }}</h4>
      <p>{{ service.description }}</p>
    </div>
  </div>

  <!-- BATCHES -->
  <div *ngIf="batches.length > 0" class="batches-section">
    <h3>Available Batches</h3>

    <div class="batch-card" *ngFor="let batch of batches">
      <p><strong>{{ batch.batchName }}</strong></p>
      <p>Start Date: {{ batch.startDate | date }}</p>
      <p>Price: ₹{{ batch.price }}</p>
      <p>Seats: {{ batch.availableSeats }}</p>

      <button
        class="btn-primary"
        [disabled]="batch.availableSeats === 0"
        (click)="bookBatch(batch.batchId)"
      >
        Book Now
      </button>
    </div>
  </div>

</div>
