import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { API } from 'src/app/core/api/api-constants.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css']
})
export class BookBatchComponent implements OnInit {

  batchId!: string;
  bookingLoading = false;

  batchDetails: any = null;

  model = {
    studentMobile: ''
  };

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private http: HttpClient
  ) {}

  ngOnInit(): void {
    this.batchId = this.route.snapshot.paramMap.get('batchId')!;
    this.loadBatchDetails();
  }

  loadBatchDetails(): void {
    this.http
      .get<any>(`${API.bookings}/details/${this.batchId}`)
      .subscribe({
        next: res => this.batchDetails = res,
        error: err => console.error('Failed to load batch details', err)
      });
  }

  confirmBooking(): void {
    if (!this.model.studentMobile || this.model.studentMobile.length < 10) {
      alert('Please enter a valid mobile number');
      return;
    }

    const userId = localStorage.getItem('userId');
    if (!userId) {
      alert('User not logged in');
      return;
    }

    this.bookingLoading = true;

    const payload = {
      userId,
      batchId: this.batchId,
      studentMobile: this.model.studentMobile
    };

    this.http.post<any>(`${API.bookings}`, payload).subscribe({
      next: res => {
        this.router.navigate(['/user/payment', res.bookingId]);
      },
      error: err => {
        console.error('Booking failed', err);
        this.bookingLoading = false;
      }
    });
  }

  cancel(): void {
    this.router.navigate(['/services']);
  }
}
<div class="book-batch-container" *ngIf="batchDetails">

  <h2 class="title">Confirm Your Booking</h2>

  <!-- Batch Info -->
  <div class="batch-card">
    <p><strong>Batch:</strong> {{ batchDetails.batchName }}</p>
    <p><strong>Instructor:</strong> {{ batchDetails.instructorName }}</p>
    <p><strong>Start Date:</strong> {{ batchDetails.startDate | date }}</p>
    <p class="price">₹ {{ batchDetails.amount }}</p>
  </div>

  <!-- Mobile Input -->
  <div class="form-group">
    <label>Student Mobile Number *</label>
    <input
      type="tel"
      placeholder="Enter mobile number"
      [(ngModel)]="model.studentMobile"
      maxlength="15"
    />
  </div>

  <!-- Actions -->
  <div class="actions">
    <button
      class="btn-primary"
      (click)="confirmBooking()"
      [disabled]="bookingLoading"
    >
      {{ bookingLoading ? 'Booking...' : 'Confirm & Proceed to Payment' }}
    </button>

    <button class="btn-secondary" (click)="cancel()">Cancel</button>
  </div>
</div>

<!-- Loading -->
<p class="loading" *ngIf="!batchDetails">
  Loading batch details...
</p>
