import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import {
  BatchManageService,
  CreateBatchRequest,
} from 'src/app/services/batches/batch-manage.service';
import { ServiceService } from 'src/app/services/services/service.service';


@Component({
  selector: 'app-create-batch',
  templateUrl: './create-batch.component.html',
  styleUrls: ['./create-batch.component.css'],
})
export class CreateBatchComponent implements OnInit {
  // ===============================
  // Form Model
  // ===============================
  batchName = '';
  description = '';
  price = 0;
  startDate = '';
  serviceId = '';

  // ===============================
  // Dropdown Data
  // ===============================
  services: any[] = [];

  // ===============================
  // UI State
  // ===============================
  isSubmitting = false;

  constructor(
    private batchManageService: BatchManageService,
    private serviceService: ServiceService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.fetchServices();
  }

  // ===============================
  // Load Services
  // ===============================
  fetchServices(): void {
    this.serviceService.getAllServices().subscribe({
      next: (services) => {
        this.services = services;
      },
      error: (error: any) => {
        console.error('Error loading services', error);
      },
    });
  }

  // ===============================
  // Submit Handler
  // ===============================
  submitBatch(): void {
    if (this.isSubmitting) return;

    const request: CreateBatchRequest = {
      batchName: this.batchName,
      description: this.description,
      price: this.price,
      startDate: this.startDate,
      serviceId: this.serviceId,
    };

    this.isSubmitting = true;

    this.batchManageService.createBatch(request).subscribe({
      next: () => {
        alert('Batch created successfully');
        this.router.navigate(['/instructor/batches']);
      },
      error: (error) => {
        console.error('Batch creation failed', error);
        this.isSubmitting = false;
      },
    });
  }
}
