import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-select-role',
  templateUrl: './select-role.component.html',
  styleUrls: ['./select-role.component.css']
})
export class SelectRoleComponent {

  selectedRole: string | null = null;

  constructor(private auth: AuthService) {}

  selectRole(role: string) {
    this.selectedRole = role;
  }

  continue() {
    if (!this.selectedRole) return;

    this.auth.setRole(this.selectedRole);  // <-- call service method
  }
}
<div class="d-flex justify-content-center align-items-center vh-100 select-role-bg">

  <div class="card p-4 shadow-lg role-card">

    <h3 class="text-center fw-bold mb-3 text-primary-custom">Choose Your Role</h3>
    <p class="text-center text-muted mb-4">How would you like to use the Hairstylist App?</p>

    <!-- User Role -->
    <div class="role-option mb-3 p-3 border rounded d-flex align-items-center"
         [class.selected]="selectedRole === 'User'"
         (click)="selectRole('User')">
        <i class="bi bi-person fs-3 me-3 text-primary-custom"></i>
        <div>
          <h5 class="mb-1">Normal User</h5>
          <small class="text-muted">Book hairstylist masterclasses and batches</small>
        </div>
    </div>

    <!-- Instructor Role -->
    <div class="role-option mb-3 p-3 border rounded d-flex align-items-center"
         [class.selected]="selectedRole === 'Instructor'"
         (click)="selectRole('Instructor')">
        <i class="bi bi-person-badge fs-3 me-3 text-primary-custom"></i>
        <div>
          <h5 class="mb-1">Instructor</h5>
          <small class="text-muted">Create batches and manage bookings</small>
        </div>
    </div>

    <!-- Continue Button -->
    <button class="btn btn-primary w-100 mt-2"
            [disabled]="!selectedRole"
            (click)="continue()">
      Continue
    </button>

  </div>

</div>
