import { Component, OnInit } from '@angular/core';
import { UserProfileService, UserProfile } from
  'src/app/services/users/user-profile.service';

@Component({
  selector: 'app-user-profile',
  templateUrl: './user-profile.component.html',
  styleUrls: ['./user-profile.component.css']
})
export class UserProfileComponent implements OnInit {

  profile!: UserProfile;
  loading = true;
  editMode = false;

  constructor(private profileApi: UserProfileService) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  loadProfile(): void {
    this.profileApi.getMyProfile().subscribe({
      next: res => {
        this.profile = res;
        this.loading = false;
      },
      error: err => {
        console.error('Profile load failed', err);
        this.loading = false;
      }
    });
  }

  saveProfile(): void {
    this.profileApi.updateMyProfile(this.profile).subscribe({
      next: () => {
        this.editMode = false;
        alert('Profile updated successfully');
      },
      error: () => alert('Update failed')
    });
  }

  onPhotoSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (!input.files || input.files.length === 0) return;

    const formData = new FormData();
    formData.append('file', input.files[0]);

    this.profileApi.uploadPhoto(formData).subscribe({
      next: res => this.profile.photoUrl = res.photoUrl,
      error: () => alert('Photo upload failed')
    });
  }
}
