import { Component, OnInit } from '@angular/core';
import { BookingService } from '../../services/booking.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-view-bookings',
  templateUrl: './view-bookings.component.html',
  styleUrls: ['./view-bookings.component.css']
})
export class ViewBookingsComponent implements OnInit {

  groupedBookings: any[] = [];

  constructor(
    private bookingApi: BookingService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadInstructorBookings();
  }

  loadInstructorBookings() {
  const instructorId = localStorage.getItem("instructorId");

  if (!instructorId) {
    alert("Instructor profile not found. Please login again.");
    this.router.navigate(['/login']);
    return;
  }

  this.bookingApi.getBookingsByInstructor(instructorId)
    .subscribe((res: any[]) => {

      const grouped: any = {};

      res.forEach((booking: any) => {
        const batchId = booking.batch.batchId;

        if (!grouped[batchId]) {
          grouped[batchId] = {
            batchName: booking.batch.batchName,
            serviceName: booking.batch.serviceName,
            startDate: booking.batch.startDate,
            bookings: []
          };
        }

        grouped[batchId].bookings.push(booking);
      });

      this.groupedBookings = Object.values(grouped);
    },
    (err) => console.error(err));
}

<div class="container py-4">

  <h2 class="page-title text-primary-custom mb-4">Student Bookings</h2>

  <!-- No bookings -->
  <div *ngIf="groupedBookings.length === 0" class="text-center text-muted mt-5">
    <i class="bi bi-info-circle fs-2"></i>
    <p class="mt-2">No students have booked your batches yet.</p>
  </div>

  <!-- Grouped By Batch -->
  <div *ngFor="let batch of groupedBookings" class="mb-4">

    <div class="card card-custom p-4 shadow-sm">

      <!-- Batch Header -->
      <h4 class="fw-bold text-primary-custom mb-1">
        {{ batch.batchName }}
      </h4>

      <!-- ✅ Service Name -->
      <p class="text-muted mb-1">
        <i class="bi bi-book"></i>
        Service: <strong>{{ batch.serviceName }}</strong>
      </p>

      <!-- ✅ Batch Start Date -->
      <p class="text-muted mb-3">
        <i class="bi bi-calendar-event"></i>
        Starts on: {{ batch.startDate | date }}
      </p>

      <!-- Students Table -->
      <table class="table table-striped align-middle">
        <thead>
          <tr>
            <th>Student</th>
            <th>Mobile</th>
            <th>Booking Date</th>
            <th>Status</th>
          </tr>
        </thead>

        <tbody>
          <tr *ngFor="let item of batch.bookings">
            <td>{{ item.student.name }}</td>
            <td>{{ item.student.mobile }}</td>
            <td>{{ item.bookingDate | date }}</td>

            <td>
              <span
                class="badge px-3 py-2"
                [ngClass]="{
                  'bg-success': item.status === 'Booked',
                  'bg-warning text-dark': item.status === 'Pending',
                  'bg-danger': item.status === 'Cancelled'
                }">
                {{ item.status }}
              </span>
            </td>
          </tr>
        </tbody>
      </table>

    </div>
  </div>

</div>
