import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BatchManageService } from 'src/app/services/batches/batch-manage.service';
import { API } from 'src/app/core/api/api-constants';
@Component({
  selector: 'app-create-batch',
  templateUrl: './create-batch.component.html',
  styleUrls: ['./create-batch.component.css']
})
export class CreateBatchComponent implements OnInit {

  // ===============================
  // Form Fields
  // ===============================
  batchName: string = '';
  description: string = '';
  price: number = 0;
  startDate: string = '';
  serviceId: string = '';

  // ===============================
  // Dropdown Data
  // ===============================
  services: any[] = [];

  // ===============================
  // UI State
  // ===============================
  submitting = false;

  constructor(
    private batchManageApi: BatchManageService,
    private serviceApi: ServiceService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadServices();
  }

  // ===============================
  // Load Services
  // ===============================
  loadServices(): void {
    this.serviceApi.getAllServices().subscribe({
      next: (res: any) => {
        this.services = res;
      },
      error: (err) => {
        console.error('Failed to load services', err);
      }
    });
  }

  // ===============================
  // Submit Form
  // ===============================
  createBatch(): void {

    if (this.submitting) return;

    const payload = {
      batchName: this.batchName,
      description: this.description,
      price: this.price,
      startDate: this.startDate,
      serviceId: this.serviceId
    };

    this.submitting = true;

    this.batchManageApi.createBatch(payload).subscribe({
      next: () => {
        alert('Batch created successfully!');
        this.router.navigate(['/instructor/batches']);
      },
      error: (err) => {
        console.error('Batch creation failed', err);
        alert('Failed to create batch. Please try again.');
        this.submitting = false;
      }
    });
  }
}
