import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import { ServiceService } from 'src/app/services/service.service';
import { BatchService } from 'src/app/services/batch.service';
import { BookingService } from 'src/app/services/booking.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css']
})
export class BookBatchComponent implements OnInit {

  services: any[] = [];
  batches: any[] = [];

  selectedServiceId: string = '';
  isLoading: boolean = false;
  searchText: string = '';

  // âœ… NEW: mobile number
  studentMobile: string = '';

  constructor(
    private serviceApi: ServiceService,
    private batchApi: BatchService,
    private bookingApi: BookingService,
    private router: Router,
    private route: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      if (params['search']) {
        this.searchText = params['search'].toLowerCase().trim();
      }
    });

    // âœ… load saved mobile if exists
    const savedMobile = localStorage.getItem('studentMobile');
    if (savedMobile) {
      this.studentMobile = savedMobile;
    }

    this.loadServices();
  }

  loadServices(): void {
    this.serviceApi.getAllServices().subscribe({
      next: (res) => {
        this.services = res;
        if (this.searchText) {
          this.handleSearchAutoSelect();
        }
      },
      error: (err) => console.error(err)
    });
  }

  handleSearchAutoSelect(): void {
    const serviceMatch = this.services.find(s =>
      s.name?.toLowerCase().includes(this.searchText)
    );

    if (serviceMatch) {
      this.selectedServiceId = serviceMatch.serviceId;
      this.loadBatches();
    }
  }

  loadBatches(): void {
    if (!this.selectedServiceId) {
      this.batches = [];
      return;
    }

    this.isLoading = true;

    this.batchApi.getAllBatches(this.selectedServiceId).subscribe({
      next: (res) => {
        this.batches = res;
        this.isLoading = false;
      },
      error: () => this.isLoading = false
    });
  }

  // âœ… FIXED BOOKING
  bookBatch(batchId: string): void {
    const userId = localStorage.getItem('userId');

    if (!userId) {
      alert('Please login again.');
      this.router.navigate(['/login']);
      return;
    }

    if (!this.studentMobile || this.studentMobile.length < 10) {
      alert('Please enter a valid mobile number');
      return;
    }

    // âœ… persist mobile
    localStorage.setItem('studentMobile', this.studentMobile);

    const bookingData = {
      userId: userId,
      batchId: batchId,
      studentMobile: this.studentMobile   // ðŸ”¥ REQUIRED
    };

    this.bookingApi.createBooking(bookingData).subscribe({
      next: (res: any) => {
        alert('Batch booked successfully!');
        this.router.navigate(['/user/payment'], {
          state: {
            bookingId: res.bookingId,
            amount: res.amount
          }
        });
      },
      error: () => {
        alert('Failed to book the batch. Please try again.');
      }
    });
  }
}
<div class="col-md-4" *ngFor="let batch of batches">
  <div class="card card-custom p-3 hover-card">

    <h5 class="fw-bold">{{ batch.batchName }}</h5>

    <p class="text-muted mb-1">
      <i class="bi bi-person-badge"></i>
      Instructor:
      <strong>{{ batch.instructor?.name ?? 'Not Assigned' }}</strong>
    </p>

    <!-- âœ… Instructor Mobile -->
    <p class="text-muted mb-1">
      <i class="bi bi-telephone"></i>
      Instructor Mobile:
      <strong>{{ batch.instructor?.contactMobile ?? 'N/A' }}</strong>
    </p>

    <p class="text-muted mb-1">
      <i class="bi bi-calendar-event"></i>
      Start Date: {{ batch.startDate | date }}
    </p>

    <p class="text-muted mb-1">
      <i class="bi bi-cash-stack"></i>
      Price: <strong>â‚¹{{ batch.price }}</strong>
    </p>

    <!-- âœ… Seats info -->
    <p class="text-muted mb-1">
      <i class="bi bi-people"></i>
      Seats:
      <strong>{{ batch.availableSeats }}</strong> /
      {{ batch.totalSeats }}
    </p>

    <button
      class="btn btn-primary-custom w-100 mt-2"
      [disabled]="batch.availableSeats === 0"
      (click)="bookBatch(batch.batchId)"
    >
      {{ batch.availableSeats === 0 ? 'Sold Out' : 'Book Now' }}
    </button>

  </div>
</div>
