import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { SearchService } from 'src/app/services/search.service';

@Component({
  selector: 'app-search-results',
  templateUrl: './search-results.component.html',
  styleUrls: ['./search-results.component.css']
})
export class SearchResultsComponent implements OnInit {

  query = '';

  services: any[] = [];
  instructors: any[] = [];
  batches: any[] = [];

  loading = true;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private searchService: SearchService
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      this.query = params['q'];

      if (this.query) {
        this.search();
      }
    });
  }

  search(): void {
    this.loading = true;

    this.searchService.search(this.query).subscribe({
      next: (res) => {
        this.services = res.services || [];
        this.instructors = res.instructors || [];
        this.batches = res.batches || [];
        this.loading = false;
      },
      error: () => {
        this.loading = false;
      }
    });
  }

  // üßë‚Äçüè´ Instructor click
  goToInstructor(instructor: any): void {
    this.router.navigate(['/public/instructor', instructor.instructorId]);
  }

  // üßæ Service click ‚Üí re-search by service name
  goToService(service: any): void {
    this.router.navigate(['/search-results'], {
      queryParams: { q: service.name }
    });
  }

  // üìÖ Book batch (UNCHANGED)
  onBook(batch: any) {
    const token = localStorage.getItem('token');

    if (!token) {
      this.router.navigate(['/auth/login'], {
        queryParams: { redirect: '/search-results?q=' + this.query }
      });
    } else {
      this.router.navigate(['/user/book-batch'], {
        state: { batch }
      });
    }
  }
}
<div class="search-results-container">

  <!-- üîÑ Loading -->
  <p *ngIf="loading">Searching...</p>

  <!-- ‚ùå No Results -->
  <p *ngIf="!loading 
            && services.length === 0 
            && instructors.length === 0 
            && batches.length === 0">
    No results found for "<strong>{{ query }}</strong>"
  </p>

  <!-- üßë‚Äçüè´ INSTRUCTORS -->
  <div *ngIf="instructors.length > 0">
    <h3>Instructors</h3>

    <div class="result-grid">
      <div
        class="result-card instructor-card"
        *ngFor="let instructor of instructors"
        (click)="goToInstructor(instructor)"
      >
        <h4>{{ instructor.name }}</h4>
        <p>{{ instructor.specialization }}</p>
      </div>
    </div>
  </div>

  <!-- üßæ SERVICES -->
  <div *ngIf="services.length > 0">
    <h3>Services</h3>

    <div class="result-grid">
      <div
        class="result-card service-card"
        *ngFor="let service of services"
        (click)="goToService(service)"
      >
        <h4>{{ service.name }}</h4>
      </div>
    </div>
  </div>

  <!-- üìÖ BATCHES -->
  <div *ngIf="batches.length > 0">
    <h3>Batches</h3>

    <div class="result-grid">
      <div class="result-card" *ngFor="let batch of batches">
        <h4>{{ batch.batchName }}</h4>
        <p>Start: {{ batch.startDate | date }}</p>
        <p>‚Çπ{{ batch.price }}</p>

        <button (click)="onBook(batch)">
          Book Now
        </button>
      </div>
    </div>
  </div>

</div>
