import { Component, OnInit } from '@angular/core';
import {
  InstructorProfile,
  InstructorProfileService
} from 'src/app/services/instructors/instructor-profile.service';

@Component({
  selector: 'app-instructor-profile',
  templateUrl: './profile.component.html',
  styleUrls: ['./profile.component.css']
})
export class InstructorProfileComponent implements OnInit {

  API_BASE = '';
  profile!: InstructorProfile;
  loading = true;
  editMode = false;

  constructor(private profileApi: InstructorProfileService) {}

  ngOnInit(): void {
    this.loadProfile();
  }

  // ================= LOAD PROFILE =================
  loadProfile(): void {
    this.profileApi.getMyProfile().subscribe({
      next: res => {
        this.profile = res;
        this.loading = false;
      },
      error: err => {
        console.error('Profile load failed', err);
        this.loading = false;
      }
    });
  }

  // ================= SAVE PROFILE =================
saveProfile(): void {
  this.profileApi.updateMyProfile(this.profile).subscribe({
    next: () => {
      this.editMode = false;
      alert('Profile updated successfully');
    },
    error: () => alert('Update failed')
  });
}



  // ================= UPLOAD PHOTO =================
  onPhotoSelected(event: Event): void {
    const input = event.target as HTMLInputElement;
    if (!input.files || input.files.length === 0) return;

    const file = input.files[0];
    const formData = new FormData();
    formData.append('file', file);

    this.profileApi.uploadPhoto(formData).subscribe({
      next: (res: any) => {
        this.profile.photoUrl = res.photoUrl;
      },
      error: () => alert('Photo upload failed')
    });
  }
}


<div class="profile-page">
  <h2 class="page-title">My Profile</h2>

  <!-- Loading -->
  <p *ngIf="loading" class="text-muted">Loading profile...</p>

  <!-- Profile Card -->
  <div *ngIf="!loading && profile" class="profile-card">
    <!-- ================= HEADER ================= -->
    <div class="profile-header">
      <!-- PROFILE PHOTO -->
      <div class="photo-box">
        <img
          [src]="
            profile.photoUrl
              ? API_BASE + profile.photoUrl
              : 'assets/Images/default-avatar.png'
          "
          class="profile-avatar"
          alt="Instructor Photo"
        />

        <!-- Upload photo -->
        <label class="upload-btn">
          ðŸ“¸ Upload photo
          <input
            type="file"
            accept="image/*"
            hidden
            (change)="onPhotoSelected($event)"
          />
        </label>
      </div>

      <!-- BASIC INFO -->
      <div class="profile-basic">
        <h3>{{ profile.fullName }}</h3>
        <p class="email">{{ profile.email }}</p>

        <span class="status" [class.active]="profile.isActive">
          {{ profile.isActive ? "Active Instructor" : "Inactive" }}
        </span>
      </div>

      <!-- EDIT BUTTON -->
      <button class="edit-btn" (click)="editMode = !editMode">
        {{ editMode ? "Cancel" : "Edit Profile" }}
      </button>
    </div>

    <!-- ================= VIEW MODE ================= -->
    <div class="profile-body" *ngIf="!editMode">
      <div class="info-row">
        <label>Display Name</label>
        <span>{{ profile.name }}</span>
      </div>

      <div class="info-row">
        <label>Specialization</label>
        <span>{{ profile.specialization }}</span>
      </div>

      <div class="info-row">
        <label>Mobile</label>
        <span>{{ profile.mobile }}</span>
      </div>

      <div class="info-row full">
        <label>About Me</label>
        <p>{{ profile.description || "No description provided." }}</p>
      </div>
    </div>

    <!-- ================= EDIT MODE ================= -->
    <form *ngIf="editMode" class="profile-body" (ngSubmit)="saveProfile()">
      <div class="info-row">
        <label>Full Name</label>
        <input
          type="text"
          [(ngModel)]="profile.fullName"
          name="fullName"
          required
        />
      </div>

      <div class="info-row">
        <label>Display Name</label>
        <input type="text" [(ngModel)]="profile.name" name="name" />
      </div>

      <div class="info-row">
        <label>Specialization</label>
        <input
          type="text"
          [(ngModel)]="profile.specialization"
          name="specialization"
        />
      </div>

      <div class="info-row">
        <label>Mobile</label>
        <input type="tel" [(ngModel)]="profile.mobile" name="mobile" />
      </div>

      <div class="info-row full">
        <label>About Me</label>
        <textarea
          rows="4"
          [(ngModel)]="profile.description"
          name="description"
        ></textarea>
      </div>

      <div class="actions">
        <button type="submit" class="save-btn">Save Changes</button>
      </div>
    </form>
  </div>
</div>
