import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  constructor(private router: Router) {}

  showNavbar(): boolean {
    return !(
      this.router.url.includes('login') ||
      this.router.url.includes('select-role')
    );
  }

  isUser(): boolean {
    return localStorage.getItem('role') === 'User';
  }

  isInstructor(): boolean {
    return localStorage.getItem('role') === 'Instructor';
  }

  getUserName(): string {
    return localStorage.getItem('fullName') || "Profile";
  }

  logout() {
    localStorage.clear();
    this.router.navigate(['/login']);
  }

  isLoggedIn(): boolean {
  return !!localStorage.getItem("token"); // or use your auth service
}

}
<!-- RIGHT SIDE AUTH / PROFILE -->
<div class="d-flex align-items-center">

  <!-- SHOW LOGIN BUTTON BEFORE LOGIN -->
  <button 
    class="btn btn-primary-custom"
    *ngIf="!isLoggedIn()"
    routerLink="/login">
      <i class="bi bi-box-arrow-in-right me-1"></i> Login
  </button>

  <!-- SHOW PROFILE DROPDOWN AFTER LOGIN -->
  <div class="dropdown" *ngIf="isLoggedIn()">
    <button class="btn btn-light dropdown-toggle d-flex align-items-center"
            type="button" data-bs-toggle="dropdown">

      <i class="bi bi-person-circle fs-4 me-2"></i>
      {{ getUserName() }}

    </button>

    <ul class="dropdown-menu dropdown-menu-end">
      <li><a class="dropdown-item" routerLink="/profile">My Profile</a></li>
      <li><hr class="dropdown-divider"></li>
      <li><button class="dropdown-item text-danger" (click)="logout()">Logout</button></li>
    </ul>
  </div>

</div>
