import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { API } from './endpoints.service';

@Injectable({
  providedIn: 'root'
})
export class BatchService {

  constructor(private http: HttpClient) {}

  getBatchesByService(serviceId: string) {
    return this.http.get(`${API.batches}/service/${serviceId}`);
  }

  getAllBatches() {
  return this.http.get(API.batches);
}

  getBatchesByInstructor(instructorId: string) {
    return this.http.get(`${API.batches}/instructor/${instructorId}`);
  }

  createBatch(data: any) {
    return this.http.post(API.batches, data);
  }

  updateBatch(batchId: string, data: any) {
    return this.http.put(`${API.batches}/${batchId}`, data);
  }

  deleteBatch(batchId: string) {
    return this.http.delete(`${API.batches}/${batchId}`);
  }
}
