import { Component, OnInit } from '@angular/core';
import { BookingUserService } from 'src/app/services/bookings/booking-user.service';

@Component({
  selector: 'app-user-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css'],
})
export class UserDashboardComponent implements OnInit {
  totalBookings = 0;
  upcomingCount = 0;
  completedCount = 0;
  upcomingBooking: any = null;
  loading = true;
  userName: string = '';

  constructor(private bookingApi: BookingUserService) {}

  ngOnInit(): void {
    this.loadDashboard();
    const user = JSON.parse(localStorage.getItem('user') || '{}');
    this.userName = user?.name || user?.fullName || '';
  }

  loadDashboard(): void {
    const userId = localStorage.getItem('userId');

    if (!userId) {
      console.error('UserId not found');
      this.loading = false;
      return;
    }

    this.bookingApi.getBookingsByUser(userId).subscribe({
      next: (res) => {
        this.totalBookings = res.length;
        this.upcomingBooking = res.find((b) => b.status === 'Confirmed');
        this.upcomingCount = res.filter((b) => b.status === 'Confirmed').length;
        this.completedCount = res.filter(
          (b) => b.status === 'Completed'
        ).length;
        this.loading = false;
      },
      error: (err) => {
        console.error('Dashboard load error', err);
        this.loading = false;
      },
    });
  }
}
<div class="dashboard-container">

  <h2 class="welcome">ðŸ‘‹ Welcome back {{ userName }}</h2>

  <!-- Loading -->
  <p *ngIf="loading" class="text-muted">Loading your dashboard...</p>

  <!-- UPCOMING BATCH -->
  <div *ngIf="!loading && upcomingBooking" class="card highlight">
    <h3>ðŸ“… Upcoming Batch</h3>
    <p><strong>{{ upcomingBooking.serviceName }}</strong></p>
    <p>Instructor: {{ upcomingBooking.instructorName }}</p>
    <p>Start Date: {{ upcomingBooking.startDate | date }}</p>
    <span class="status confirmed">Confirmed</span>
  </div>

  <!-- NO UPCOMING -->
  <div *ngIf="!loading && !upcomingBooking" class="card empty">
    <p>No upcoming batches</p>
    <a routerLink="/services" class="btn-primary">Browse Services</a>
  </div>

  <!-- STATS -->
  <div class="stats-grid">
    <div class="stat-card">
      <h4>Total Bookings</h4>
      <p>{{ totalBookings }}</p>
    </div>

    <div class="stat-card">
      <h4>Upcoming</h4>
      <p>{{ upcomingCount }}</p>
    </div>

    <div class="stat-card">
      <h4>Completed</h4>
      <p>{{ completedCount }}</p>
    </div>
  </div>

  <!-- QUICK ACTIONS -->
  <div class="actions">
    <a routerLink="/services" class="btn-secondary">Browse Services</a>
    <a routerLink="/my-bookings" class="btn-secondary">View My Bookings</a>
    <a routerLink="/profile" class="btn-secondary">Edit Profile</a>
  </div>

</div>
