import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { API } from './endpoints.service';
import { BookingDetails } from '../user/payment/booking-details';
import { Observable } from 'rxjs/internal/Observable';

@Injectable({
  providedIn: 'root',
})
export class BookingService {
  baseUrl: any;

  constructor(private http: HttpClient) {}

  createBooking(data: any) {
    return this.http.post(`${API.bookings}`, data);
  }

  getBookingsByUser(userId: string) {
    return this.http.get(`${API.bookings}/user/${userId}`);
  }

  getBookingsByInstructor(instructorId: string) {
    return this.http.get(`${API.bookings}/instructor/${instructorId}`);
  }
  getBookingById(id: string) {
    return this.http.get<BookingDetails>(`${API.bookings}/details/${id}`);
  }
  cancelBooking(bookingId: string) {
  return this.http.put(
    `${API.bookings}/cancel/${bookingId}`,
    {}
  );
}

}
