import { Component, AfterViewInit } from '@angular/core';
import { AuthGoogleService } from '../../../core/auth/auth-google.service';
import { AuthApiService } from '../../../core/auth/auth-api.service';
import { AuthStorageService } from '../../../core/auth/auth-storage.service';
import { AuthNavigationService } from '../../../core/auth/auth-navigation.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements AfterViewInit {


  constructor(
    private googleAuth: AuthGoogleService,
    private authApi: AuthApiService,
    private authStorage: AuthStorageService,
    private authNavigation: AuthNavigationService,
    private router: Router
  ) {}

  ngAfterViewInit(): void {
    this.googleAuth.initGoogleLogin((idToken: string) => {
      this.handleGoogleLogin(idToken);
    });
  }

  private handleGoogleLogin(idToken: string): void {
  this.authApi.googleLogin(idToken).subscribe({
    next: (res: any) => {

      // Save auth basics
      this.authStorage.setToken(res.token);
      this.authStorage.setUserId(res.userId);

      // ðŸ”‘ If role NOT selected â†’ go to select-role
      if (!res.role) {
       this.router.navigate(['/auth/select-role']);
;
        return;
      }

      // ðŸ”‘ Role exists â†’ normal login flow
      this.authStorage.setRole(res.role);

      const returnUrl = this.authStorage.returnUrl;
      this.authNavigation.navigateAfterLogin(returnUrl);
      this.authStorage.clearReturnUrl();
    },
    error: () => {
      alert('Google login failed');
    }
  });
}


}
<div class="login-container">
  <div class="login-card">

    <h2 class="title">Welcome to Hairvona</h2>
    <p class="subtitle">
      Login using your Google account to continue
    </p>

    <!-- Google Button -->
    <div id="googleBtn" class="google-btn"></div>

  </div>
</div>
