import { Component, OnInit } from '@angular/core';
import {
  InstructorDashboardService,
  InstructorDashboardSummary
} from '../../../services/instructors/instructor-dashboard.service';

@Component({
  selector: 'app-instructor-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class InstructorDashboardComponent implements OnInit {

  summary!: InstructorDashboardSummary;
  loading = true;

  constructor(
    private dashboardApi: InstructorDashboardService
  ) {}

  ngOnInit(): void {
    this.loadSummary();
  }

  loadSummary(): void {
    this.dashboardApi.getDashboardSummary().subscribe({
      next: res => {
        this.summary = res;
        this.loading = false;
      },
      error: err => {
        console.error('Dashboard summary error', err);
        this.loading = false;
      }
    });
  }
}
<div class="dashboard-container">

  <h2 class="page-title">Instructor Dashboard</h2>

  <!-- Loading -->
  <div *ngIf="loading" class="text-muted">
    Loading dashboard...
  </div>

  <!-- Summary Cards -->
  <div class="summary-cards" *ngIf="!loading">

    <div class="card summary-card">
      <h4>Total Students</h4>
      <p class="count">{{ summary.totalStudents }}</p>
    </div>

    <div class="card summary-card">
      <h4>Total Earnings</h4>
      <p class="count">₹ {{ summary.totalEarnings }}</p>
    </div>

  </div>

  <!-- ============================= -->
  <!-- ACTION SECTIONS -->
  <!-- ============================= -->
  <div class="action-section" *ngIf="!loading">

    <!-- Create Batch -->
    <div class="card action-card">
      <h4>Create Batch</h4>
      <p class="action-desc">
        Create a new batch and start enrolling students.
      </p>
      <button
        class="btn btn-primary"
        routerLink="/instructor/batches/create"
      >
        Create Batch
      </button>
    </div>

    <!-- Create Service -->
    <div class="card action-card">
      <h4>Create Service</h4>
      <p class="action-desc">
        Add a new service to offer masterclasses.
      </p>
      <button
        class="btn btn-secondary"
        routerLink="/instructor/services/create"
      >
        Create Service
      </button>
    </div>

  </div>

</div>
