import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-search-results',
  templateUrl: './search-results.component.html'
})
export class SearchResultsComponent implements OnInit {

  query = '';
  batches: any = [];

  constructor(
    private route: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.queryParams.subscribe(params => {
      this.query = params['q'];
      console.log('Search query:', this.query);

      if (this.query) {
        // ðŸ”œ call backend search API here
      }
    });
  }

  onBook(batch: any) {
    const token = localStorage.getItem('token');

    if (!token) {
      this.router.navigate(['/auth/login'], {
        queryParams: { redirect: '/public/search-results?q=' + this.query }
      });
    } else {
      this.router.navigate(['/user/book-batch'], {
        state: { batch }
      });
    }
  }
}
<div class="batch-card" *ngFor="let batch of batches">
  <h4>{{ batch.batchName }}</h4>
  <p>Start: {{ batch.startDate | date }}</p>

  <button (click)="onBook(batch)">
    Book Now
  </button>
</div>
