import { Component } from '@angular/core';
import { AuthStorageService } from '../../../core/auth/auth-storage.service';
import { AuthNavigationService } from '../../../core/auth/auth-navigation.service';

@Component({
  selector: 'app-select-role',
  templateUrl: './select-role.component.html',
  styleUrls: ['./select-role.component.css']
})
export class SelectRoleComponent {

  constructor(
    private storage: AuthStorageService,
    private navigation: AuthNavigationService
  ) {}

  selectRole(role: 'User' | 'Instructor'): void {
    this.storage.setRole(role);
    this.navigation.navigateByRole(role);
  }
}
<div class="role-container">
  <div class="role-card">

    <h2 class="title">Choose Your Role</h2>
    <p class="subtitle">
      Please select how you want to continue
    </p>

    <div class="role-buttons">
      <button
        class="role-btn user"
        (click)="selectRole('User')">
        I am a Student
      </button>

      <button
        class="role-btn instructor"
        (click)="selectRole('Instructor')">
        I am an Instructor
      </button>
    </div>

  </div>
</div>
