import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { BatchService } from 'src/app/services/batch.service';
import { ServiceService } from 'src/app/services/service.service';

@Component({
  selector: 'app-edit-batch',
  templateUrl: './edit-batch.component.html',
  styleUrls: ['./edit-batch.component.css']
})
export class EditBatchComponent implements OnInit {

  batchId = "";
  batchName = "";
  description = "";
  price = 0;
  startDate = "";
  serviceId = "";

  services: any[] = [];
  loaded = false;

  // ✅ NEW: minimum selectable date (today)
  minDate: string = "";

  constructor(
    private route: ActivatedRoute,
    private batchApi: BatchService,
    private serviceApi: ServiceService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.batchId = this.route.snapshot.params['id'];

    // ✅ Set today's date (YYYY-MM-DD)
    const today = new Date();
    this.minDate = today.toISOString().split('T')[0];

    this.loadServices();
    this.loadBatch();
  }

  loadServices() {
    this.serviceApi.getAllServices().subscribe(res => this.services = res);
  }

  loadBatch() {
    this.batchApi.getBatchById(this.batchId).subscribe((batch: any) => {
      this.batchName = batch.batchName;
      this.description = batch.description;
      this.price = batch.price;
      this.startDate = batch.startDate.split("T")[0];
      this.serviceId = batch.serviceId;

      this.loaded = true;
    });
  }

  updateBatch() {
    const instructorId = localStorage.getItem("userId");

    const updated = {
      batchId: this.batchId,
      batchName: this.batchName,
      description: this.description,
      startDate: this.startDate,
      price: this.price,
      instructorId,
      serviceId: this.serviceId
    };

    this.batchApi.updateBatch(this.batchId, updated).subscribe({
      next: () => {
        alert("Batch updated successfully!");
        this.router.navigate(['/instructor/manage-batches']);
      }
    });
  }
}
<div class="container mt-4">

  <h2 class="fw-bold mb-4">Edit Batch</h2>

  <div class="card shadow-sm p-4" *ngIf="loaded">

    <div class="mb-3">
      <label class="form-label fw-semibold">Service</label>
      <select class="form-select" [(ngModel)]="serviceId">
        <option *ngFor="let s of services" [value]="s.serviceId">
          {{ s.name }}
        </option>
      </select>
    </div>

    <div class="mb-3">
      <label class="form-label fw-semibold">Batch Name</label>
      <input type="text" class="form-control" [(ngModel)]="batchName">
    </div>

    <div class="mb-3">
      <label class="form-label fw-semibold">Description</label>
      <textarea rows="4" class="form-control" [(ngModel)]="description"></textarea>
    </div>

    <div class="mb-3">
      <label class="form-label fw-semibold">Price (₹)</label>
      <input type="number" class="form-control" [(ngModel)]="price">
    </div>


    <div class="mb-3">
      <label class="form-label fw-semibold">Start Date</label>
      <input
        type="date"
        class="form-control"
        [(ngModel)]="startDate"
        [min]="minDate">
    </div>

    <button class="btn btn-primary w-100" (click)="updateBatch()">
      Save Changes
    </button>

  </div>

</div>
