import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { InstructorDashboardService } from '../../../services/instructors/instructor-dashboard.service';

@Component({
  selector: 'app-instructor-batches',
  templateUrl: './batches.component.html',
  styleUrls: ['./batches.component.css']
})
export class InstructorBatchesComponent implements OnInit {

  batches: any[] = [];
  loading = true;

  selectedBatch: any = null;

  constructor(
    private dashboardApi: InstructorDashboardService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBatchWiseStats();
  }

  loadBatchWiseStats(): void {
    this.dashboardApi.getBatchWiseStats().subscribe({
      next: res => {
        this.batches = res;
        this.loading = false;
      },
      error: err => {
        console.error('Batch-wise stats error', err);
        this.loading = false;
      }
    });
  }

  // ü™ü Open popup
  openBatchPopup(batch: any): void {
    this.selectedBatch = batch;
  }

  closePopup(): void {
    this.selectedBatch = null;
  }

  // ‚úèÔ∏è Edit batch
  editBatch(): void {
    this.closePopup();
    this.router.navigate([
      '/batches/edit-batch',
      this.selectedBatch.batchId
    ]);
  }

  // üóëÔ∏è Delete batch
  deleteBatch(): void {
    if (!confirm('Are you sure you want to delete this batch?')) {
      return;
    }

    this.dashboardApi.deleteBatch(this.selectedBatch.batchId).subscribe({
      next: () => {
        this.closePopup();
        this.loadBatchWiseStats();
      },
      error: err => {
        alert('Delete failed');
        console.error(err);
      }
    });
  }
}
<div class="page-container">
  <h2 class="page-title">My Batches</h2>

  <!-- Loading -->
  <p *ngIf="loading" class="text-muted">Loading batches...</p>

  <!-- No Data -->
  <p *ngIf="!loading && batches.length === 0" class="text-muted">
    No batches found.
  </p>

  <!-- Batch Table -->
  <table *ngIf="!loading && batches.length > 0" class="table table-striped">
    <thead>
      <tr>
        <th>Batch Name</th>
        <th>No. of Students</th>
        <th>Total Earnings</th>
      </tr>
    </thead>

    <tbody>
      <tr *ngFor="let batch of batches">
        <td>
          <span
            class="batch-name-link"
            (click)="openBatchPopup(batch)"
            title="Click to manage batch"
          >
            {{ batch.batchName }}
          </span>
        </td>

        <td>
          {{ batch.students }}
        </td>

        <td>
          ‚Çπ {{ batch.totalEarnings }}
        </td>
      </tr>
    </tbody>
  </table>
</div>

<!-- ü™ü BATCH ACTION POPUP -->
<div class="popup-backdrop" *ngIf="selectedBatch">
  <div class="popup-card">
    <h3>{{ selectedBatch.batchName }}</h3>

    <!-- üìä Batch Details -->
    <div class="popup-stats">
      <div class="stat-item">
        <span class="stat-label">Students Enrolled</span>
        <span class="stat-value">
          {{ selectedBatch.students }}
        </span>
      </div>

      <div class="stat-item">
        <span class="stat-label">Total Earnings</span>
        <span class="stat-value">
          ‚Çπ {{ selectedBatch.totalEarnings }}
        </span>
      </div>
    </div>

    <!-- Actions -->
    <div class="popup-actions">
      <button class="btn-edit" (click)="editBatch()">
        ‚úèÔ∏è Edit Batch
      </button>

      <button
        class="btn-delete"
        [disabled]="selectedBatch.students > 0"
        title="Cannot delete batch with enrolled students"
        (click)="deleteBatch()"
      >
        üóëÔ∏è Delete Batch
      </button>

      <button class="btn-cancel" (click)="closePopup()">
        Cancel
      </button>
    </div>

    <!-- Warning -->
    <p
      class="delete-warning"
      *ngIf="selectedBatch.students > 0"
    >
      ‚ö†Ô∏è This batch has enrolled students and cannot be deleted.
    </p>
  </div>
</div>
