import { Component } from '@angular/core';
import { Router } from '@angular/router';
import {
  AuthStorageService,
  UserRole
} from '../../core/auth/auth-storage.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  role: UserRole = null;
  menuOpen = false;

  constructor(
    private router: Router,
    private authStorage: AuthStorageService
  ) {
    // üîÅ React to login / logout / role change
    this.authStorage.role$.subscribe(role => {
      this.role = role;
    });
  }

  // üîê LOGIN
  onLogin(): void {
    this.router.navigate(['/auth/login']);
  }

  // üìä DASHBOARD (User / Instructor)
  goToDashboard(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/dashboard']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/dashboard']);
    }
  }

  // üë§ PROFILE (User / Instructor)
  goToProfile(): void {
    if (this.role === 'User') {
      this.router.navigate(['/user/profile']);
    } else if (this.role === 'Instructor') {
      this.router.navigate(['/instructor/profile']);
    }
  }

  // üö™ LOGOUT
  logout(): void {
    this.authStorage.clearAll(); // üî• clears storage + updates navbar
    this.router.navigate(['/']);
  }
}
<header class="navbar">
  <div class="nav-left">
    <img src="assets/Images/Site-Logo.png" alt="Hairvona Logo" class="logo" />
    <a class="nav-link desktop-only" routerLink="/">Home</a>

    <a *ngIf="role" class="nav-link desktop-only" (click)="goToDashboard()">
      Dashboard
    </a>
  </div>

  <div class="nav-right desktop-only">
    <input
      type="text"
      placeholder="Search service or instructor..."
      class="search-input"
    />

    <button class="btn btn-search">Search</button>

    <a *ngIf="role" class="nav-link" (click)="goToProfile()">Profile</a>

    <button *ngIf="!role" class="btn btn-login" (click)="onLogin()">Login</button>
    <button *ngIf="role" class="btn btn-login" (click)="logout()">Logout</button>
  </div>

  <!-- ‚ò∞ Hamburger (Mobile Only) -->
  <div class="hamburger mobile-only" (click)="menuOpen = !menuOpen">
    ‚ò∞
  </div>
</header>

<!-- üì± Mobile Right Menu -->
<div class="mobile-menu" [class.open]="menuOpen">
  <a routerLink="/" (click)="menuOpen=false">Home</a>

  <a *ngIf="role" (click)="goToDashboard(); menuOpen=false">
    Dashboard
  </a>

  <a *ngIf="role" (click)="goToProfile(); menuOpen=false">
    Profile
  </a>

  <a *ngIf="!role" (click)="onLogin(); menuOpen=false">
    Login
  </a>

  <a *ngIf="role" (click)="logout(); menuOpen=false">
    Logout
  </a>
</div>
