import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BatchService } from 'src/app/services/batch.service';

@Component({
  selector: 'app-manage-batches',
  templateUrl: './manage-batches.component.html',
  styleUrls: ['./manage-batches.component.css']
})
export class ManageBatchesComponent implements OnInit {

  batches: any[] = [];
  isLoading = false;

  constructor(
    private batchApi: BatchService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadBatches();
  }

  // Load batches created by instructor
  loadBatches() {
    const instructorId = localStorage.getItem("userId");

    if (!instructorId) {
      alert("Please login again.");
      this.router.navigate(['/login']);
      return;
    }

    this.isLoading = true;

    this.batchApi.getBatchesByInstructor(instructorId).subscribe({
      next: (res) => {
        this.batches = res;
        this.isLoading = false;
      },
      error: (err) => {
        console.error("Error loading batches:", err);
        this.isLoading = false;
      }
    });
  }

  // Navigate to edit page
  editBatch(batchId: string) {
    this.router.navigate(['/instructor/edit-batch', batchId]);
  }

  // Delete a batch
  deleteBatch(batchId: string) {
    if (!confirm("Are you sure you want to delete this batch?")) {
      return;
    }

    this.batchApi.deleteBatch(batchId).subscribe({
      next: () => {
        alert("Batch deleted successfully");
        this.loadBatches(); // Reload list
      },
      error: (err) => console.error("Error deleting batch:", err)
    });
  }

}
<div class="container mt-4">

  <h2 class="fw-bold mb-3">Manage Your Batches</h2>

  <div class="row g-3">
    <div class="col-md-4" *ngFor="let batch of batches">
      <div class="card shadow-sm p-3">

        <h5 class="fw-bold">{{ batch.batchName }}</h5>
        <p class="text-muted">{{ batch.description }}</p>

        <p><strong>Service:</strong> {{ batch.service?.name }}</p>
        <p><strong>Price:</strong> ₹{{ batch.price }}</p>
        <p><strong>Start:</strong> {{ batch.startDate | date }}</p>

        <div class="d-flex gap-2 mt-2">
          <button class="btn btn-primary w-50" (click)="editBatch(batch.batchId)">Edit</button>
          <button class="btn btn-danger w-50" (click)="deleteBatch(batch.batchId)">Delete</button>
        </div>

      </div>
    </div>
  </div>

</div>
