import { Component, HostListener, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Router } from '@angular/router';
import { API, API_BASE } from 'src/app/core/api/api-constants.service';
import { BookingUserService } from 'src/app/services/bookings/booking-user.service';

@Component({
  selector: 'app-book-batch',
  templateUrl: './book-batch.component.html',
  styleUrls: ['./book-batch.component.css'],
})
export class BookBatchComponent implements OnInit {

  Api_BASE = API_BASE;
  services: any[] = [];
  selectedService: any = null;
  batches: any[] = [];

  loading = true;
  isMobile = false;

  activeBatchId: string | null = null;
  studentMobile = '';
  processing = false;

  constructor(
    private http: HttpClient,
    private router: Router,
    private bookingApi: BookingUserService
  ) {}

  ngOnInit(): void {
    this.loadServices();
    this.checkScreen();
  }

  @HostListener('window:resize')
  checkScreen() {
    this.isMobile = window.innerWidth <= 768;
  }

  loadServices(): void {
    this.http.get<any[]>(API.services).subscribe({
      next: res => {
        this.services = res.map(s => ({ ...s, expanded: false }));
        this.loading = false;
      },
      error: () => this.loading = false
    });
  }

  selectService(service: any): void {
    this.selectedService = service;
    this.activeBatchId = null;
    this.studentMobile = '';
    this.loadBatches(service.serviceId);
  }

  maskMobile(mobile?: string): string {
  if (!mobile || mobile.length < 10) return '**********';
  return mobile.substring(0, 2) + '******' + mobile.substring(8);
}


loadBatches(serviceId: string): void {
  this.http
    .get<any[]>(`${API.batches}/service/${serviceId}`)
    .subscribe(res => {
      this.batches = res;

      // ðŸ”‘ Attach instructor preview to selected service
      if (res.length > 0) {
        this.selectedService.instructor = res[0].instructor;
      }
    });
}


  toggleDescription(service: any): void {
    service.expanded = !service.expanded;
  }

  openMobileInput(batchId: string): void {
    this.activeBatchId = batchId;
    this.studentMobile = '';
  }

  confirmBooking(batchId: string): void {
    if (this.processing) return;

    const userId = localStorage.getItem('userId');
    if (!userId || this.studentMobile.length !== 10) {
      alert('Enter valid mobile number');
      return;
    }

    this.processing = true;

    this.bookingApi.createBooking({
      userId,
      batchId,
      studentMobile: this.studentMobile
    }).subscribe({
      next: (res: any) => {
        this.processing = false;
        this.router.navigate(['/user/payment', res.bookingId]);
      },
      error: () => this.processing = false
    });
  }
}
